(31, 0, 0, 'GalleryCustomTV', '', 0, 0, 0, '/**\n * Handles plugin events for Gallery''s Custom TV\n * \n * @package gallery\n */\n$corePath = $modx->getOption(''gallery.core_path'',null,$modx->getOption(''core_path'').''components/gallery/'');\nswitch ($modx->event->name) {\n    case ''OnTVInputRenderList'':\n        $modx->event->output($corePath.''elements/tv/input/'');\n        break;\n    case ''OnTVOutputRenderList'':\n        $modx->event->output($corePath.''elements/tv/output/'');\n        break;\n    case ''OnTVInputPropertiesList'':\n        $modx->event->output($corePath.''elements/tv/inputoptions/'');\n        break;\n    case ''OnTVOutputRenderPropertiesList'':\n        $modx->event->output($corePath.''elements/tv/properties/'');\n        break;\n    case ''OnManagerPageBeforeRender'':\n        $gallery = $modx->getService(''gallery'',''Gallery'',$modx->getOption(''gallery.core_path'',null,$modx->getOption(''core_path'').''components/gallery/'').''model/gallery/'',$scriptProperties);\n        if (!($gallery instanceof Gallery)) return '''';\n\n        $snippetIds = '''';\n        $gallerySnippet = $modx->getObject(''modSnippet'',array(''name'' => ''Gallery''));\n        if ($gallerySnippet) {\n            $snippetIds .= ''GAL.snippetGallery = "''.$gallerySnippet->get(''id'').''";''."\\n";\n        }\n        $galleryItemSnippet = $modx->getObject(''modSnippet'',array(''name'' => ''GalleryItem''));\n        if ($galleryItemSnippet) {\n            $snippetIds .= ''GAL.snippetGalleryItem = "''.$galleryItemSnippet->get(''id'').''";''."\\n";\n        }\n\n        $jsDir = $modx->getOption(''gallery.assets_url'',null,$modx->getOption(''assets_url'').''components/gallery/'').''js/mgr/'';\n        $modx->controller->addLexiconTopic(''gallery:default'');\n        $modx->controller->addJavascript($jsDir.''gallery.js'');\n        $modx->controller->addJavascript($jsDir.''tree.js'');\n        $modx->controller->addHtml(''<script type="text/javascript">\n        Ext.onReady(function() {\n            GAL.config.connector_url = "''.$gallery->config[''connectorUrl''].''";\n            ''.$snippetIds.''\n        });\n        </script>'');\n        break;\n    case ''OnDocFormPrerender'':\n        $gallery = $modx->getService(''gallery'',''Gallery'',$modx->getOption(''gallery.core_path'',null,$modx->getOption(''core_path'').''components/gallery/'').''model/gallery/'',$scriptProperties);\n        if (!($gallery instanceof Gallery)) return '''';\n\n        /* assign gallery lang to JS */\n        $modx->controller->addLexiconTopic(''gallery:tv'');\n\n        /* @var modAction $action */\n        $action = $modx->getObject(''modAction'',array(\n            ''namespace'' => ''gallery'',\n            ''controller'' => ''index'',\n        ));\n        $modx->controller->addHtml(''<script type="text/javascript">\n        Ext.onReady(function() {\n            GAL.config = {};\n            GAL.config.connector_url = "''.$gallery->config[''connectorUrl''].''";\n            GAL.action = "''.($action ? $action->get(''id'') : 0).''";\n        });\n        </script>'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/tv/Spotlight.js'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/gallery.js'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/widgets/album/album.items.view.js'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/widgets/album/album.tree.js'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/tv/gal.browser.js'');\n        $modx->controller->addJavascript($gallery->config[''assetsUrl''].''js/mgr/tv/galtv.js'');\n        $modx->controller->addCss($gallery->config[''cssUrl''].''mgr.css'');\n        break;\n}\nreturn;', 0, NULL, 0, '', 0, ''),
(32, 0, 0, 'CheckboxSortable', 'Plugin to handle CheckboxSortable TV type.', 0, 0, 0, '/**\r\n * @var modX $modx\r\n * @var array $scriptProperties\r\n */\r\n$corePath = $modx->getOption(''checkboxsortable.core_path'', null, $modx->getOption(''core_path'') . ''components/checkboxsortable/'');\r\n$assetsURL = $modx->getOption(''checkboxsortable.assets_url'', null, $modx->getOption(''assets_url'') . ''components/checkboxsortable/'');\r\n$modx->lexicon->load(''checkboxsortable:default'');\r\n$lexicons = $modx->lexicon->loadCache(''checkboxsortable'', ''default'');\r\n\r\nswitch ($modx->event->name) {\r\n\r\n    case ''OnDocFormPrerender'':\r\n    case ''OnTVFormPrerender'':\r\n        $modx->controller->addHtml(''<script type="text/javascript">\r\n    Ext.applyIf(MODx.lang, ''. $modx->toJSON($lexicons) .'');\r\n</script>'');\r\n        //$modx->regClientStartupScript($assetsURL . ''checkboxsortable.js'');\r\n        if (''OnTVFormPrerender'' == $modx->event->name) {\r\n            // Load JS for input properties\r\n            $modx->regClientStartupScript($assetsURL . ''checkboxsortable-properties.js'');\r\n        }\r\n        break;\r\n\r\n    case ''OnTVInputRenderList'':\r\n        $modx->event->output($corePath. ''elements/tv/input/'');\r\n        break;\r\n\r\n    case ''OnTVInputPropertiesList'':\r\n        $modx->event->output($corePath. ''elements/tv/properties/'');\r\n        break;\r\n\r\n//    case ''OnTVOutputRenderList'':\r\n//        $modx->event->output($corePath. ''elements/tv/output/'');\r\n//        break;\r\n}\r\nreturn;', 0, NULL, 0, '', 0, ''),
(33, 0, 0, 'DaterangeTV', '', 0, 59, 0, '/**\r\n * Daterange TV runtime hooks\r\n * Registers custom TV input & output types and includes javascripts on document\r\n * edit pages so that the TV can be used from within other extras (i.e. MIGX,\r\n * Collections)\r\n *\r\n * @package daterangetv\r\n * @subpackage plugin\r\n *\r\n * @event OnManagerPageBeforeRender\r\n * @event OnTVInputRenderList\r\n * @event OnTVOutputRenderList\r\n * @event OnTVInputPropertiesList\r\n * @event OnTVOutputRenderPropertiesList\r\n * @event OnDocFormRender\r\n *\r\n * @var modX $modx\r\n */\r\n\r\n$corePath = $modx->getOption(''daterangetv.core_path'', null, $modx->getOption(''core_path'') . ''components/daterangetv/'');\r\n$daterangetv = $modx->getService(''daterangetv'', ''DaterangeTV'', $corePath . ''model/daterangetv/'', array(\r\n    ''core_path'' => $corePath\r\n));\r\n\r\nswitch ($modx->event->name) {\r\n    case ''OnManagerPageBeforeRender'':\r\n        $modx->controller->addLexiconTopic(''daterangetv:tvrenders'');\r\n        $daterangetv->includeScriptAssets();\r\n        break;\r\n    case ''OnTVInputRenderList'':\r\n        $modx->event->output($corePath . ''elements/tv/input/'');\r\n        break;\r\n    case ''OnTVOutputRenderList'':\r\n        $modx->event->output($corePath . ''elements/tv/output/'');\r\n        break;\r\n    case ''OnTVInputPropertiesList'':\r\n        $modx->event->output($corePath . ''elements/tv/input/options/'');\r\n        break;\r\n    case ''OnTVOutputRenderPropertiesList'':\r\n        $modx->event->output($corePath . ''elements/tv/output/options/'');\r\n        break;\r\n    case ''OnDocFormRender'':\r\n        $daterangetv->includeScriptAssets();\r\n        break;\r\n};', 0, 'a:0:{}', 0, '', 0, ''),
(53, 1, 0, 'modTelegram', '', 0, 85, 0, 'switch ($modx->event->name) {\n\n    case ''OnMODXInit'':\n        $modx->loadClass(''modUser'');\n\n        $modx->map[''modUser''][''composites''][''TelegramManager''] = array(\n            ''class''       => ''modTelegramManager'',\n            ''local''       => ''id'',\n            ''foreign''     => ''user'',\n            ''cardinality'' => ''one'',\n            ''owner''       => ''local'',\n        );\n\n        $modx->map[''modUser''][''composites''][''TelegramUser''] = array(\n            ''class''       => ''modTelegramUser'',\n            ''local''       => ''id'',\n            ''foreign''     => ''user'',\n            ''cardinality'' => ''one'',\n            ''owner''       => ''local'',\n        );\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/modtelegram/elements/plugins/plugin.modtelegram.php'),
(35, 1, 0, 'controlErrorLog', '', 0, 61, 0, 'switch ($modx->event->name) {\r\n    case ''OnManagerPageBeforeRender'':\r\n        if ($modx->hasPermission(''error_log_view'')) {\r\n            $modx->controller->addLexiconTopic(''controlerrorlog:default'');\r\n            $modx->controller->addCss($modx->getOption(''assets_url'').''components/controlerrorlog/css/mgr/main.css'');\r\n            $modx->controller->addJavascript($modx->getOption(''assets_url'').''components/controlerrorlog/js/mgr/cel.default.js'');\r\n\r\n            $response = $modx->runProcessor(''mgr/errorlog/get'', array(''includeContent''=>false), array(''processors_path'' => $modx->getOption(''core_path'') . ''components/controlerrorlog/processors/''));\r\n            $resObj = $response->getObject();\r\n            $_html = "<script>	var cel_config = " . $modx->toJSON($resObj) . "; </script>";\r\n            $modx->controller->addHtml($_html);\r\n        }\r\n        break;\r\n    case ''OnHandleRequest'':\r\n        $email = $modx->getOption(''controlerrorlog.admin_email'');\r\n        if ($modx->context->get(''key'') == ''mgr'' || empty($email) || !$modx->getOption(''controlerrorlog.control_frontend'')) return;\r\n\r\n        $f = $modx->getOption(xPDO::OPT_CACHE_PATH) . ''logs/error.log'';\r\n        if (file_exists($f)) {\r\n            $casheHash = $modx->cacheManager->get(''error_log'');\r\n            $hash = md5_file($f);\r\n            if (filesize($f) > 0 && !empty($casheHash)  &&  $casheHash != $hash) {\r\n                $modx->lexicon->load(''controlerrorlog:default'');\r\n                /** @var modPHPMailer $mail */\r\n                $mail = $modx->getService(''mail'', ''mail.modPHPMailer'');\r\n                $mail->setHTML(true);\r\n\r\n                $mail->set(modMail::MAIL_SUBJECT, $modx->lexicon(''error_log_email_subject''));\r\n                $mail->set(modMail::MAIL_BODY, $modx->lexicon(''error_log_email_body'', array(''siteName'' => $modx->config[''site_name''])));\r\n                $mail->set(modMail::MAIL_SENDER, $modx->getOption(''emailsender''));\r\n                $mail->set(modMail::MAIL_FROM, $modx->getOption(''emailsender''));\r\n                $mail->set(modMail::MAIL_FROM_NAME, $modx->getOption(''site_name''));\r\n\r\n                $mail->address(''to'', $email);\r\n                $mail->address(''reply-to'', $modx->getOption(''emailsender''));\r\n\r\n                if (!$mail->send()) {\r\n                    print (''An error occurred while trying to send the email: ''.$modx->mail->mailer->ErrorInfo);\r\n                }\r\n                $mail->reset();\r\n            }\r\n            if ($casheHash != $hash) {\r\n                $modx->cacheManager->set(''error_log'', $hash, 0);\r\n            }\r\n        }\r\n        break;\r\n}', 0, NULL, 0, '', 0, 'core/components/controlerrorlog/elements/plugins/plugin.controlerrorlog.php'),
(36, 1, 0, 'elementNotes', '', 0, 62, 0, 'switch ($modx->event->name) {\r\n	// add the "Note" tab\r\n	case ''OnTempFormPrerender'':\r\n		$enTabs = ''modx-template-tabs'';\r\n	case ''OnChunkFormPrerender'':\r\n		if (!isset($enTabs)) $enTabs = ''modx-chunk-tabs'';\r\n	case ''OnSnipFormPrerender'':\r\n		if (!isset($enTabs)) $enTabs = ''modx-snippet-tabs'';\r\n	case ''OnPluginFormPrerender'':\r\n		if (!isset($enTabs)) $enTabs = ''modx-plugin-tabs'';\r\n		if ($mode == modSystemEvent::MODE_UPD) {\r\n			$modx->controller->addLexiconTopic(''elementnotes:default'');\r\n			$modx->controller->addJavascript($modx->getOption(''assets_url'') . ''components/elementnotes/js/mgr/elementnotes.js'');\r\n			$modx->controller->addLastJavascript($modx->getOption(''assets_url'') . ''components/elementnotes/js/mgr/widgets/elementnotes.panel.js'');\r\n			$_html = ''<script>\r\n				var elemNotes = {};\r\n				elemNotes.config = {"connector_url" : "''.$modx->getOption(''assets_url'').''components/elementnotes/connector.php"};\r\n				Ext.onReady(function() {\r\n					MODx.addTab("''.$enTabs.''",{\r\n						id: "elementnotes-tab",\r\n						title: _("Notes"),\r\n						items: [{\r\n							xtype: "elementnotes-page",\r\n							width: "100%"\r\n						}]\r\n					});\r\n				});</script>'';\r\n			$modx->controller->addHtml($_html);\r\n		}\r\n		break;\r\n	// Remove the element note\r\n	case ''OnChunkRemove'':\r\n		$type = ''chunk'';\r\n		$id = $chunk->id;\r\n	case ''OnPluginRemove'':\r\n		if (!isset($type)) {\r\n			$type = ''plugin'';\r\n			$id = $plugin->id;\r\n		}\r\n	case ''OnSnippetRemove'':\r\n		if (!isset($type)) {\r\n			$type = ''snippet'';\r\n			$id = $snippet->id;\r\n		}\r\n	case ''OnTemplateRemove'':\r\n		if (!isset($type)) {\r\n			$type = ''template'';\r\n			$id = $template->id;\r\n		}\r\n\r\n		/** @var elementNotes $elementNotes */\r\n		$elementNotes = $modx->getService(''elementnotes'', ''elementNotes'', $modx->getOption(''core_path'') . ''components/elementnotes/model/elementnotes/'');\r\n		if (isset($type) && isset($id))	$elementNotes->removeNote($type,$id);\r\n		break;\r\n}', 0, NULL, 0, '', 0, 'core/components/elementnotes/elements/plugins/plugin.elementnotes.php'),
(48, 0, 0, 'dropbox', 'dropbox_desc', 0, 0, 0, '/**\n * @var modX $modx\n */\n\nswitch($modx->event->name){\n    \n    case ''OnSiteRefresh'':\n        \n        if(!function_exists(''cleanUpDirectory'')){\n            function cleanUpDirectory($path)\n            {\n            	if (!file_exists($path)) {\n            		return;\n            	}\n            	$iterator = new RecursiveDirectoryIterator($path);\n            	foreach (new RecursiveIteratorIterator($iterator, RecursiveIteratorIterator::CHILD_FIRST) as $file) {\n            		if ($file->isDir()) {\n            			@rmdir($file->getPathname());\n            		} else {\n            			@unlink($file->getPathname());\n            		}\n            	}\n            }\n        }\n        \n    	$cachePath = $modx->getOption(''assets_path'', null, MODX_ASSETS_PATH) . ''components/dropbox/cache'';\n    	if (file_exists($cachePath)) {\n    	    cleanUpDirectory($cachePath);\n    // 		foreach (new DirectoryIterator($cachePath) as $file) {\n    // 			if (\n    // 			    $file->isDir()\n    // 			    AND !$file->isDot()\n    // 			) {\n    // 				cleanUpDirectory($file->getPathname());\n    // 				// cleanUpDirectory($file->getPathname() );\n    // 				// $modx->log(1, $file->getPathname());\n    // 			}\n    // 		}\n    	}\n        \n        \n        break;\n        \n    default:;\n}\n \nreturn;', 0, NULL, 0, '', 0, ''),
(49, 1, 0, 'vpSystem', '', 0, 51, 0, '/** @var array $scriptProperties */\n$fqn = $modx->getOption(''virtualpage_class'', null, ''virtualpage.virtualpage'', true);\n$path = $modx->getOption(''virtualpage_class_path'', null,\n    $modx->getOption(''core_path'', null, MODX_CORE_PATH) . ''components/virtualpage/'');\n/** @var virtualpage $virtualpage */\nif (!$virtualpage = $modx->getService($fqn, '''', $path . ''model/'', array(''core_path'' => $path))) {\n    return false;\n}\n\n$className = ''virtualpage'' . $modx->event->name;\n$modx->loadClass(''virtualpagePlugin'', $path . ''model/virtualpage/systems/'', true, true);\n$modx->loadClass($className, $path . ''model/virtualpage/systems/'', true, true);\nif (class_exists($className)) {\n    /** @var $virtualpage $plugin */\n    $plugin = new $className($modx, $scriptProperties);\n    $plugin->run();\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, 'core/components/virtualpage/elements/plugins/plugin.system.php');
INSERT INTO `modx_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(54, 1, 0, 'frontendmanager', '', 0, 87, 0, 'if (!$modx->user->hasSessionContext(''mgr'') || !$modx->user->isMember(''Administrator'')) return;\nswitch ($modx->event->name) {\n    case ''OnWebPagePrerender'':\n        $frontendManager = $modx->getService(''frontendmanager'',''frontendManager'', MODX_CORE_PATH . ''components/frontendmanager/model/frontendmanager/'', array());\n        if(!$frontendManager) return;\n		$contentTypes = explode('','', $modx->getOption(''frontendmanager_contenttypes''));\n        if (in_array($modx->resource->content_type, $contentTypes)) {\n			$modx->resource->_output .=  $frontendManager->initialize($modx->context->key);\n        }\n        break;\n    case ''OnBeforeManagerPageInit'':\n        if ($_GET[''frame'']) {\n			$modx->regClientCSS(MODX_ASSETS_URL.''components/frontendmanager/css/mgr/''.$modx->getOption(''frontendmanager_manager_css'', NULL, ''manager.css''));\n			$modx->regClientStartupScript(MODX_ASSETS_URL.''components/frontendmanager/js/mgr/''.$modx->getOption(''frontendmanager_manager_js'', NULL, ''manager.js''));\n        }\n        break;\n    default:\n        break;\n}\nreturn;', 0, NULL, 0, '', 0, 'core/components/frontendmanager/elements/plugins/plugin.frontendmanager.php'),
(40, 1, 0, 'Tickets', '', 0, 68, 0, 'switch ($modx->event->name) {\n\n	case ''OnSiteRefresh'':\n		if ($modx->cacheManager->refresh(array(''default/tickets'' => array()))) {\n			$modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(''refresh_default'') . '': Tickets'');\n		}\n		break;\n\n\n	case ''OnDocFormSave'':\n		/* @var Ticket $resource */\n		if ($mode == ''new'' && $resource->class_key == "Ticket") {\n			$modx->cacheManager->delete(''tickets/latest.tickets'');\n		}\n		/* @var TicketsSection $resource */\n		if ($mode == ''upd'' && $resource->class_key == ''TicketsSection'') {\n			if (method_exists($resource, ''clearCache'')) {\n				$resource->clearCache();\n			}\n		}\n		break;\n\n\n	case ''OnWebPagePrerender'':\n		$output = &$modx->resource->_output;\n		$output = str_replace(\n			array(''*(*(*(*(*(*'', ''*)*)*)*)*)*'', ''~(~(~(~(~(~'', ''~)~)~)~)~)~''),\n			array(''['', '']'', ''{'', ''}''),\n			$output\n		);\n		break;\n\n\n	case ''OnPageNotFound'':\n		// It is working only with friendly urls enabled\n		$q = trim(@$_REQUEST[$modx->context->getOption(''request_param_alias'', ''q'')]);\n		$matches = explode(''/'', rtrim($q, ''/''));\n		if (count($matches) < 2) {\n			return;\n		}\n\n		$ticket_uri = array_pop($matches);\n		$section_uri = implode(''/'', $matches) . ''/'';\n\n		if ($section_id = $modx->findResource($section_uri)) {\n			/** @var TicketsSection $section */\n			if ($section = $modx->getObject(''TicketsSection'', $section_id)) {\n				if (is_numeric($ticket_uri)) {\n					$ticket_id = $ticket_uri;\n				}\n				elseif (preg_match(''#^\\d+#'', $ticket_uri, $tmp)) {\n					$ticket_id = $tmp[0];\n				}\n				else {\n					$properties = $section->getProperties(''tickets'');\n					if (!empty($properties[''uri'']) && strpos($properties[''uri''], ''%id'') !== false) {\n						$pcre = str_replace(''%id'', ''([0-9]+)'', $properties[''uri'']);\n						$pcre = preg_replace(''#(\\%[a-z]+)#'', ''(?:.*?)'', $pcre);\n						if (preg_match($pcre, $ticket_uri, $matches)) {\n							$ticket_id = $matches[1];\n						}\n					}\n				}\n				if (!empty($ticket_id)) {\n					if ($ticket = $modx->getObject(''Ticket'', array(''id'' => $ticket_id, ''deleted'' => 0))) {\n						if ($ticket->published) {\n							$modx->sendRedirect($modx->makeUrl($ticket_id), array(''responseCode'' => ''HTTP/1.1 301 Moved Permanently''));\n						}\n						elseif ($unp_id = $modx->getOption(''tickets.unpublished_ticket_page'')) {\n							$modx->sendForward($unp_id);\n						}\n					}\n				}\n			}\n		}\n		break;\n\n\n	case ''OnLoadWebDocument'':\n		$authenticated = $modx->user->isAuthenticated($modx->context->get(''key''));\n		$key = ''Tickets_User'';\n\n		if (!$authenticated && !$modx->getOption(''tickets.count_guests'')) {\n			return;\n		}\n\n		if (empty($_COOKIE[$key])) {\n			if (!empty($_SESSION[$key])) {\n				$guest_key = $_SESSION[$key];\n			}\n			else {\n				$guest_key = $_SESSION[$key] = md5(rand() . time() . rand());\n			}\n			setcookie($key, $guest_key, time() + (86400 * 365), ''/'');\n		}\n		else {\n			$guest_key = $_COOKIE[$key];\n		}\n\n		if (empty($_SESSION[$key])) {\n			$_SESSION[$key] = $guest_key;\n		}\n\n		if ($authenticated) {\n			/** @var TicketAuthor $profile */\n			if (!$profile = $modx->user->getOne(''AuthorProfile'')) {\n				$profile = $modx->newObject(''TicketAuthor'');\n				$modx->user->addOne($profile);\n			}\n			$profile->set(''visitedon'', time());\n			$profile->save();\n		}\n		break;\n\n\n	case ''OnWebPageComplete'':\n		/** @var Tickets $Tickets */\n		$Tickets = $modx->getService(''tickets'');\n		$Tickets->logView($modx->resource->get(''id''));\n		break;\n\n\n	case ''OnUserSave'':\n		/** @var modUser $user */\n		if ($mode == ''new'' && $user && !$user->getOne(''AuthorProfile'')) {\n			$profile = $modx->newObject(''TicketAuthor'');\n			$user->addOne($profile);\n			$profile->save();\n		}\n		break;\n\n}', 0, NULL, 0, '', 0, 'core/components/tickets/elements/plugins/plugin.tickets.php'),
