(70, 0, 0, 'ImagePlus', 'Snippet as alternative to Image+ TV Output Type', 0, 0, 0, '/**\n * ImagePlus Snippet as alternative to Image+ TV Output Type\n *\n * Copyright 2013-2015 by Alan Pich <alan.pich@gmail.com>\n * Copyright 2015-2016 by Thomas Jakobi <thomas.jakobi@partout.info>\n *\n * @package imageplus\n * @subpackage snippet\n *\n * @author Alan Pich <alan.pich@gmail.com>\n * @author Thomas Jakobi <thomas.jakobi@partout.info>\n * @copyright Alan Pich 2013-2015\n * @copyright Thomas Jakobi 2015-2016\n *\n * @var modX $modx\n * @var array $scriptProperties\n */\n\n$corePath = $modx->getOption(''imageplus.core_path'', null, $modx->getOption(''core_path'') . ''components/imageplus/'');\n$imageplus = $modx->getService(''imageplus'', ''ImagePlus'', $corePath . ''model/imageplus/'', array(\n    ''core_path'' => $corePath\n));\n\n$tvname = $modx->getOption(''tvname'', $scriptProperties, '''', true);\n$docid = $modx->getOption(''docid'', $scriptProperties, $modx->resource->get(''id''), true);\n$type = $modx->getOption(''type'', $scriptProperties, '''', true);\n$options = $modx->getOption(''options'', $scriptProperties, '''', true);\n$tpl = $modx->getOption(''tpl'', $scriptProperties, ''ImagePlus.image'', true);\n$value = $modx->getOption(''value'', $scriptProperties, '''', true);\n$debug = $modx->getOption(''debug'', $scriptProperties, $imageplus->getOption(''debug''), false);\n\nif ($value) {\n    // Value is set by snippet property\n    $data = json_decode($value);\n    if (!$data) {\n        if ($debug) {\n            $modx->log(xPDO::LOG_LEVEL_ERROR, ''Unable to decode JSON in snippet property'', '''', ''Image+'');\n            return ''Unable to decode JSON in snippet property'';\n        }\n    }\n    // No TV is used\n    $tv = null;\n} else {\n    // Value is retreived from template variable\n    $tv = $modx->getObject(''modTemplateVar'', array(''name'' => $tvname));\n    if ($tv) {\n        // Get the raw content of the TV\n        $value = $tv->getValue($docid);\n        $value = $tv->processBindings($value, $docid);\n    } else {\n        if ($debug) {\n            $modx->log(xPDO::LOG_LEVEL_ERROR, "Template Variable ''{$tvname}'' not found.", '''', ''Image+'');\n            return "Template Variable ''{$tvname}'' not found.";\n        }\n    }\n}\n\n$output = '''';\n// Render output\nswitch ($type) {\n    case ''check'':\n        $data = json_decode($value);\n        $output = ($data && $data->sourceImg->src) ? ''image'' : ''noimage'';\n        break;\n    case ''tpl'':\n        $output = ($value) ? $imageplus->getImageURL($value, array_merge($scriptProperties, array(\n            ''docid'' => $docid,\n            ''phpThumbParams'' => $options,\n            ''outputChunk'' => $tpl\n        )), $tv) : '''';\n        break;\n    case ''thumb'':\n    default:\n        $output = ($value) ? $imageplus->getImageURL($value, array_merge($scriptProperties, array(\n            ''docid'' => $docid,\n            ''phpThumbParams'' => $options\n        )), $tv) : '''';\n        break;\n}\nreturn $output;', 0, 'a:6:{s:6:"tvname";a:7:{s:4:"name";s:6:"tvname";s:4:"desc";s:26:"imageplus.imageplus.tvname";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}s:5:"docid";a:7:{s:4:"name";s:5:"docid";s:4:"desc";s:25:"imageplus.imageplus.docid";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}s:4:"type";a:7:{s:4:"name";s:4:"type";s:4:"desc";s:24:"imageplus.imageplus.type";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}s:7:"options";a:7:{s:4:"name";s:7:"options";s:4:"desc";s:27:"imageplus.imageplus.options";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:23:"imageplus.imageplus.tpl";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:15:"ImagePlus.image";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}s:8:"fromJson";a:7:{s:4:"name";s:8:"fromJson";s:4:"desc";s:28:"imageplus.imageplus.fromJson";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:20:"imageplus:properties";s:4:"area";s:0:"";}}', '', 0, '');
