но от непонятных последовательностей символов\n- Картинки семантично раскладываются по папкам в соответствии с оригинальными картинками\n- Удалены постоянные проверки и отчистки всего кеша\n- Отключена поддержка AWS Amazon\n- Добавлен ряд параметров отвечающих за качество и тип превьюхи по умолчанию\n- Код сниппета оптимизирован под множественные вызовы";s:9:"signature";s:19:"phpthumbon-1.3.1-pl";s:13:"initialConfig";s:15:"[object Object]";s:4:"text";s:20:"Продолжить";s:2:"id";s:19:"package-install-btn";s:6:"hidden";s:5:"false";s:7:"handler";s:1749:"function (va){\n        var r;\n        var g = Ext.getCmp(''modx-package-grid'');\n        if (!g) return false;\n        \n        if (va.signature != undefined && va.signature != '''') {\n            r = {signature: va.signature};\n        } else {\n            r = g.menu.record.data ? g.menu.record.data : g.menu.record;\n        }\n		var topic = ''/workspace/package/install/''+r.signature+''/'';\n        g.loadConsole(Ext.getBody(),topic);\n\n		va = va || {};\n        Ext.apply(va,{\n            action: ''workspace/packages/install''\n            ,signature: r.signature\n            ,register: ''mgr''\n            ,topic: topic\n        });\n\n        MODx.Ajax.request({\n            url: MODx.config.connector_url\n            ,params: va\n            ,listeners: {\n                ''success'': {fn:function() {\n                    var bc = Ext.getCmp(''packages-breadcrumbs'');\n                    var trail= bc.data.trail;\n                    trail.pop();\n\n                    if (trail.length > 1) {\n                        last = trail[trail.length - 1];\n\n                        if (last != undefined && last.rec != undefined) {\n                            bc.data.trail.pop();\n                            bc.data.trail.shift();\n                            bc.updateDetail(bc.data);\n\n                            var grid = Ext.getCmp(''modx-package-grid'');\n                            grid.install(last.rec);\n                            return;\n                        }\n                    }\n\n                    this.activate();\n					Ext.getCmp(''modx-package-grid'').refresh();\n                },scope:this}\n                ,''failure'': {fn:function() {\n                    this.activate();\n                },scope:this}\n            }\n        });\n\n        return true;\n	}";s:8:"disabled";s:5:"false";s:5:"scope";s:15:"[object Object]";s:8:"minWidth";s:2:"75";s:10:"removeMode";s:9:"container";s:10:"hideParent";s:4:"true";s:6:"events";s:15:"[object Object]";s:7:"ownerCt";s:15:"[object Object]";s:9:"container";s:15:"[object Object]";s:8:"rendered";s:4:"true";s:8:"template";s:15:"[object Object]";s:5:"btnEl";s:15:"[object Object]";s:4:"mons";s:15:"[object Object]";s:2:"el";s:15:"[object Object]";s:4:"icon";s:0:"";s:7:"iconCls";s:0:"";s:8:"boxReady";s:4:"true";s:8:"lastSize";s:15:"[object Object]";s:11:"useSetClass";s:4:"true";s:6:"oldCls";s:12:"x-btn-noicon";s:3:"doc";s:15:"[object Object]";s:19:"monitoringMouseOver";s:4:"true";s:6:"action";s:26:"workspace/packages/install";s:8:"register";s:3:"mgr";s:5:"topic";s:47:"/workspace/package/install/phpthumbon-1.3.1-pl/";s:14:"package_action";i:0;}', 'phpThumbOn', 'a:38:{s:2:"id";s:24:"5496b872dc532f2c59060674";s:7:"package";s:24:"5241d84962cf2423f0000842";s:12:"display_name";s:19:"phpthumbon-1.3.1-pl";s:4:"name";s:10:"phpThumbOn";s:7:"version";s:5:"1.3.1";s:13:"version_major";s:1:"1";s:13:"version_minor";s:1:"3";s:13:"version_patch";s:1:"1";s:7:"release";s:2:"pl";s:8:"vrelease";s:2:"pl";s:14:"vrelease_index";s:0:"";s:6:"author";s:9:"ilyautkin";s:11:"description";s:556:"<p></p><ul><li>Queuing support</li><li>Fixed mode AJAX</li><li>The incorrect perception of the input parameter with the initial slash</li><li>File name prevyuhi maximum Scrubbing of unknown character sequences</li><li>Pictures semantically decomposed into folders according to the original picture</li><li>Removed constant checking and cleaning up all the cache</li><li>Not support AWS Amazon</li><li>Added a number of parameters responsible for the quality and type of prevyuhi default</li><li>Code snippet is optimized for multiple calls</li></ul><p></p>";s:12:"instructions";s:389:"<p>For a complete replacement phpthumbof will need to change the call &#91;&#91;phpThumbOf? &amp;...&#93;&#93; to &#91;&#91;phpthumbon? &amp;...&#93;&#93; or you can create a snippet:</p><p>return $modx-&gt;runSnippet(''phpThumbOn'', $scriptProperties);</p><p></p><p><b>Processing job queue</b>:&nbsp;http://example.com/assets/components/phpthumbon/connector.php?action=login&amp;ctx=web</p>";s:9:"changelog";s:47:"<p></p><ul><li>Use modPhpThumb</li></ul><p></p>";s:9:"createdon";s:24:"2014-12-21T12:09:22+0000";s:9:"createdby";s:9:"ilyautkin";s:8:"editedon";s:24:"2016-03-27T17:58:07+0000";s:10:"releasedon";s:24:"2014-12-21T12:09:22+0000";s:9:"downloads";s:4:"8593";s:8:"approved";s:4:"true";s:7:"audited";s:5:"false";s:8:"featured";s:5:"false";s:10:"deprecated";s:5:"false";s:7:"license";s:5:"GPLv2";s:7:"smf_url";s:0:"";s:10:"repository";s:24:"4d4c3fa6b2b0830da9000001";s:8:"supports";s:3:"2.2";s:8:"location";s:60:"http://modx.com/extras/download/?id=5496b873dc532f2c59060676";s:9:"signature";s:19:"phpthumbon-1.3.1-pl";s:11:"supports_db";s:5:"mysql";s:16:"minimum_supports";s:3:"2.2";s:9:"breaks_at";s:8:"10000000";s:10:"screenshot";s:0:"";s:4:"file";a:7:{s:2:"id";s:24:"5496b873dc532f2c59060676";s:7:"version";s:24:"5496b872dc532f2c59060674";s:8:"filename";s:33:"phpthumbon-1.3.1-pl.transport.zip";s:9:"downloads";s:4:"5074";s:6:"lastip";s:12:"81.177.24.60";s:9:"transport";s:4:"true";s:8:"location";s:60:"http://modx.com/extras/download/?id=5496b873dc532f2c59060676";}s:17:"package-signature";s:19:"phpthumbon-1.3.1-pl";s:10:"categories";s:28:"content,multimedia,utilities";s:4:"tags";s:0:"";}', 1, 3, 1, 'pl', 0);
