 || {};\n        Ext.apply(va,{\n            action: ''workspace/packages/install''\n            ,signature: r.signature\n            ,register: ''mgr''\n            ,topic: topic\n        });\n\n        MODx.Ajax.request({\n            url: MODx.config.connector_url\n            ,params: va\n            ,listeners: {\n                ''success'': {fn:function() {\n                    var bc = Ext.getCmp(''packages-breadcrumbs'');\n                    var trail= bc.data.trail;\n                    trail.pop();\n\n                    if (trail.length > 1) {\n                        last = trail[trail.length - 1];\n\n                        if (last != undefined && last.rec != undefined) {\n                            bc.data.trail.pop();\n                            bc.data.trail.shift();\n                            bc.updateDetail(bc.data);\n\n                            var grid = Ext.getCmp(''modx-package-grid'');\n                            grid.install(last.rec);\n                            return;\n                        }\n                    }\n\n                    this.activate();\n					Ext.getCmp(''modx-package-grid'').refresh();\n                },scope:this}\n                ,''failure'': {fn:function() {\n                    this.activate();\n                },scope:this}\n            }\n        });\n\n        return true;\n	}";s:8:"disabled";s:5:"false";s:5:"scope";s:15:"[object Object]";s:9:"autoWidth";s:4:"true";s:10:"autoHeight";s:4:"true";s:8:"minWidth";s:2:"75";s:10:"removeMode";s:9:"container";s:10:"hideParent";s:4:"true";s:6:"events";s:15:"[object Object]";s:7:"ownerCt";s:15:"[object Object]";s:9:"container";s:15:"[object Object]";s:8:"rendered";s:4:"true";s:8:"template";s:15:"[object Object]";s:5:"btnEl";s:15:"[object Object]";s:4:"mons";s:15:"[object Object]";s:2:"el";s:15:"[object Object]";s:4:"icon";s:0:"";s:7:"iconCls";s:0:"";s:8:"boxReady";s:4:"true";s:11:"useSetClass";s:4:"true";s:6:"oldCls";s:12:"x-btn-noicon";s:3:"doc";s:15:"[object Object]";s:19:"monitoringMouseOver";s:4:"true";s:6:"action";s:26:"workspace/packages/install";s:8:"register";s:3:"mgr";s:5:"topic";s:52:"/workspace/package/install/controlerrorlog-1.1.2-pl/";s:8:"lastSize";s:15:"[object Object]";s:14:"package_action";i:1;}', 'controlErrorLog', 'a:38:{s:2:"id";s:24:"584d2603bc8dd341548b4567";s:7:"package";s:24:"55b755a4dc532f69510da2ef";s:12:"display_name";s:24:"controlerrorlog-1.1.2-pl";s:4:"name";s:15:"controlErrorLog";s:7:"version";s:5:"1.1.2";s:13:"version_major";s:1:"1";s:13:"version_minor";s:1:"1";s:13:"version_patch";s:1:"2";s:7:"release";s:2:"pl";s:8:"vrelease";s:2:"pl";s:14:"vrelease_index";s:0:"";s:6:"author";s:10:"sergant210";s:11:"description";s:1210:"<p style="">Now you will not miss any errors. If the log is too large you will see a certain number of the last lines you have specified in the system setting "Last lines" (thanks Bob Ray for the idea). By default, 15.</p><p><b>System settings</b></p><p></p><p><b>last_lines</b> - Displays the specified number of last lines when the error log is too large to display.   </p><p><b>auto_refresh</b> - Check the state of the error log with the specified frequency. </p><p><b>refresh_freq </b>- Error log refresh frequency in seconds. By default, set to 60 seconds.</p><p><b>control_frontend </b>- If true the email notification is activated. Must be specified the admin email.</p><p><b>admin_email </b>- Admin email to notify about changes in the error log. If empty the notification would not work.</p><p><b>Cron task</b></p><p>Use the script <i>core/components/controlerrorlog/cron/checkerrorlog.php</i> to check the error log for new errors.</p><p style=""><b>Important! </b>The error indicator in the manager interface is displayed only for administrators (sudo) and users with permission "error_log_view".</p><p style="">See details on <a href="https://github.com/sergant210/controlErrorLog">Github</a>.</p>";s:12:"instructions";s:37:"<p>Install via Package Management</p>";s:9:"changelog";s